    // \u672C\u8EABdrawImage\u5BB9\u5668\u4E0D\u5728\u9875\u9762\u6E32\u67D3\u7684\uFF0C\u6240\u4EE5\u6CA1\u6709\u5FC5\u8981\u901A\u8FC7\u72B6\u6001\u66F4\u65B0\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u5728\u5F00\u59CB\u62D6\u62FD\u65F6\u83B7\u53D6\u81EA\u5B9A\u4E49\u56FE\u50CFdom\u5E76\u4FEE\u6539\u5185\u5BB9\u540E\u5373\u65F6\u83B7\u53D6\u5373\u53EF
